import{_ as a,c as i,o as d,x as e,a as o}from"./chunks/framework.d7e4e340.js";const f=JSON.parse('{"title":"8. Propiedad Colectiva (Collective Ownership)","description":"","frontmatter":{},"headers":[],"relativePath":"practices/collective-ownership.md"}'),r={name:"practices/collective-ownership.md"},c=e("h1",{id:"_8-propiedad-colectiva-collective-ownership",tabindex:"-1"},[o("8. Propiedad Colectiva "),e("em",null,"(Collective Ownership)"),o(),e("a",{class:"header-anchor",href:"#_8-propiedad-colectiva-collective-ownership","aria-label":'Permalink to "8. Propiedad Colectiva _(Collective Ownership)_"'},"​")],-1),l=e("p",null,"La propiedad colectiva del código tiene por objetivo que todos los miembros del equipo conozcan “qué” y “cómo” se está desarrollando el sistema, evitando así, lo que sucede en muchas empresas, que existen “programadores dueños de un código” y cuando surge un problema, nadie más que él puede resolverlo, puesto que el resto del equipo, desconoce cómo fue hecho y cuáles han sido sus requerimientos a lo largo del desarrollo.",-1),n=e("p",null,"No debe existir un programador “dueño” de un determinado código o funcionalidad. Cualquiera puede modificar cualquier módulo en cualquier momento y nadie tiene la propiedad de ningún módulo. El código que el equipo genere para llevar a cabo un proyecto es propiedad de cada uno de los miembros del equipo. De esta forma cualquier miembro del equipo podrá modificar un módulo o porción de código generado por otro miembro si lo cree conveniente.",-1),s=e("blockquote",null,[e("p",null,"Dentro de un equipo no existen derechos de autor ni prohibiciones de ningún tipo a la hora de modificar el código de un compañero. Todo el código desarrollado por cada miembro del equipo es cedido para el bien del propio equipo y poniéndolo a su disposición para cualquier mejora o comentario.")],-1),t=e("p",null,"La propiedad colectiva del código permite al grupo trabajar mas rápidamente y rendir de una forma mas eficaz debido a que no se producen los retardos o esperas ocasionados por la propiedad privada de un código y la incapacidad de modificarlo sin pedir permiso al miembro del equipo “creador” del código.",-1),p=[c,l,n,s,t];function u(m,h,_,q,v,g){return d(),i("div",null,p)}const x=a(r,[["render",u]]);export{f as __pageData,x as default};
